// src/pages/CreateListing.jsx (Simplified for General Item Listings)

import { useState, useEffect, useRef } from 'react';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import {
  getStorage,
  ref,
  uploadBytesResumable,
  getDownloadURL,
} from 'firebase/storage';
import { addDoc, collection, serverTimestamp } from 'firebase/firestore';
import { db } from '../firebase.config';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { v4 as uuidv4 } from 'uuid';

function CreateListing() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: '',
    price: '',
    type: 'sale',
    images: {},
  });

  const [loading, setLoading] = useState(false);
  const auth = getAuth();
  const navigate = useNavigate();
  const isMounted = useRef(true);

  useEffect(() => {
    if (isMounted.current) {
      onAuthStateChanged(auth, (user) => {
        if (user) {
          setFormData((prev) => ({ ...prev, userRef: user.uid }));
        } else {
          navigate('/sign-in');
        }
      });
    }
    return () => (isMounted.current = false);
  }, [auth, navigate]);

  const handleChange = (e) => {
    if (e.target.files) {
      setFormData((prev) => ({ ...prev, images: e.target.files }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [e.target.name]: e.target.value,
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (formData.images.length > 6) {
      setLoading(false);
      toast.error('Maximum 6 images allowed');
      return;
    }

    const storeImage = async (image) => {
      return new Promise((resolve, reject) => {
        const storage = getStorage();
        const fileName = `${auth.currentUser.uid}-${image.name}-${uuidv4()}`;
        const storageRef = ref(storage, 'images/' + fileName);
        const uploadTask = uploadBytesResumable(storageRef, image);

        uploadTask.on(
          'state_changed',
          null,
          (error) => reject(error),
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(resolve);
          }
        );
      });
    };

    const imgUrls = await Promise.all(
      [...formData.images].map((img) => storeImage(img))
    ).catch(() => {
      setLoading(false);
      toast.error('Image upload failed');
      return;
    });

    const listingData = {
      ...formData,
      imgUrls,
      timestamp: serverTimestamp(),
    };

    delete listingData.images;

    try {
      const docRef = await addDoc(collection(db, 'listings'), listingData);
      setLoading(false);
      toast.success('Listing created successfully');
      navigate(`/category/${listingData.type}/${docRef.id}`);
    } catch (err) {
      console.error('Listing creation failed:', err);
      toast.error('Could not save listing');
      setLoading(false);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Create a Listing</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Item Name" value={formData.name} onChange={handleChange} className="mb-2 p-2 border w-full" required />
        <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} className="mb-2 p-2 border w-full" required />
        <input name="location" placeholder="Location" value={formData.location} onChange={handleChange} className="mb-2 p-2 border w-full" required />
        <input name="price" type="number" placeholder="Price" value={formData.price} onChange={handleChange} className="mb-2 p-2 border w-full" required />
        <div className="flex gap-4 mb-4">
          <label>
            <input type="radio" name="type" value="sale" checked={formData.type === 'sale'} onChange={handleChange} /> Sell
          </label>
          <label>
            <input type="radio" name="type" value="rent" checked={formData.type === 'rent'} onChange={handleChange} /> Rent
          </label>
        </div>
        <input name="images" type="file" onChange={handleChange} multiple accept=".jpg,.png,.jpeg" className="mb-4" required />
        <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded">Submit Listing</button>
      </form>
    </div>
  );
}

export default CreateListing;
